def intpredict(mine_amount, non, pred):
    if mine_amount == 1:
        empty = []
        for x in range(5):
            grid = [
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}"
            ]
            create_grid = randrange(0, len(grid))
            grid[create_grid] = choice(
                [f"{pred}"])
            empty.append("".join(grid))

    elif mine_amount == 2:
        empty = []
        for x in range(5):
            grid = [
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}"
            ]
            create_grid = randrange(0, len(grid))
            grid[create_grid] = choice([
                f"{pred}"
            ])
            empty.append("".join(grid))

    elif mine_amount == 3:
        empty = []
        for x in range(5):
            grid = [
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}"
            ]
            create_grid = randrange(0, len(grid))
            grid[create_grid] = choice([
                f"{pred}", f"{non}", f"{pred}"
            ])
            empty.append("".join(grid))

    elif mine_amount == 4:
        empty = []
        for x in range(5):
            grid = [
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}"
            ]
            create_grid = randrange(0, len(grid))
            grid[create_grid] = choice([
                f"{pred}", f"{non}",
                f"{non}", f"{pred}"
            ])
            empty.append("".join(grid))

    elif mine_amount == 5:
        empty = []
        for x in range(5):
            grid = [
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}",
                f"{non}"
            ]
            create_grid = randrange(0, len(grid))
            grid[create_grid] = choice([
                f"{pred}", f"{non}",
                f"{non}"
            ])
            empty.append("".join(grid))
    result = "\n".join(empty)

    return result


def checkactive(token):
    userdata = scraper.get(f'https://api.bloxflip.com/games/mines', headers={'x-auth-token': token}).json()
    game = userdata['hasGame']
    if game == True:
        result = {"hasGame": True}
        return result
    else:
        result = {"hasGame": False}
        return result

def checkroundid(token, round):
    userdata = scraper.get(f'https://api.bloxflip.com/games/mines', headers={'x-auth-token': token}).json()
    roundid = userdata['game']['uuid']
    if round == roundid:
        result = {"Same": True}
        return result
    else:
        result = {"Same": False}
        return result

def placemine(token, spot):
    spoted = scraper.get(f'https://rest-bf.blox.land/games/mines/action', headers={'x-auth-token': token}, json={"cashout": False , "mine":spot}).json()
    return spoted
